function isMobile() {
    return /Mobi|Android/i.test(navigator.userAgent);
}

$(document).ready(function() {
    if (isMobile()) {
        $("html").addClass("mobile");
    } else {
        const sections = ["Главная", "Проекты", "Профессии", "Образование", "Стажировка", "Текущая позиция", "Связаться со мной"];
        const sectionsRU = ["Главная", "Проекты", "Профессии", "Образование", "Стажировка", "Текущая позиция", "Связаться со мной"];
        const isRussian = window.location.href.indexOf("ru.html") >= 0;

        $(".section").addClass("initing");
        $("#pagepiling").pagepiling({
            loopBottom: true,
            navigation: {
                position: "left",
                tooltips: isRussian ? sectionsRU : sections
            },
            onLeave: function(index, nextIndex, direction) {
                $(".section").each(function(i) {
                    $(this).toggleClass("leaving", i + 1 === nextIndex);
                });
            }
        });
        $(".initing").removeClass("initing");
    }
});
